#include <iostream>#include <cstring>#include "lb5.h"using std::cin;using std::cout;using std::endl;//Ð¯. Hello, fucking beach. I am really fine. I hate you. I really hate you. Suck my dick, idiot. Ha ha ha idiot. Suck. Suck.%int main() {    char *text = new char[10000];    char sign = ' ';    int i = 0;    int count_of_sentences = 0;    cout << "Enter text. After finishing input put %" << endl;    while (1) {        sign = cin.peek();        if (sign == '.') {            count_of_sentences += 1;            text[i] = '.';            text[i + 1] = '\n';            i += 2;            cin.ignore();        } else if (sign == ' ') {            if (text[i - 1] == '\n') {                cin.ignore();            } else {                text[i] = sign;                i += 1;                cin.ignore();            }        } else if (sign == '%') {            break;        } else {            text[i] = sign;            i += 1;            cin.ignore();        }    }    int lengths[count_of_sentences];    int ln = 1;    int j = 0;    for (int i = 0; i < strlen(text); ++i) {        if (text[i] != '.') {            ln += 1;        } else {            lengths[j] = ln;            j += 1;            ln = 0;        }    }    sorting(lengths, text, count_of_sentences);}